drop table if exists product;

create table product
(
    id          bigint generated by default as identity,
    description TEXT,
    name        varchar(255) not null,
    price       float(53),
    primary key (id)
);

insert into product (name, description, price)
values ('Масло конопляное', 'Данное масло называют уникальным диетическим продуктом высокой биологической ценности. Это исконно русский, невероятно полезный, но, к сожалению, не заслуженно забытый продукт. Масло испокон веков производили на Руси, где оно славилось своими свойствами. На сегодняшний день производство масла семян конопли всё же возрождается и этот продукт всё чаще можно встретить в магазинах.  Это светло-зелёное масло со слабовыраженной кислинкой и приятным ароматом получают из семечек конопли. Стоит отметить, что семя растения, в отличие от соцветий и листьев, не содержат каннабиола, психотропного вещества, которое собственно и оказывает наркотическое действие.', 1150),
       ('Масло льняное', 'Благодаря своему уникальному химическому составу, льняное масло выделяется даже на фоне самых популярных суперфудов. Концентрация полезных веществ в нём действительно впечатляет, поэтому его нелишне включить в ежедневный рацион.', 500),
       ('Масло кокосовое', 'В составе кокосового масла содержатся витамины А, E и K, группы B (холин), а также железо. Но особенно высоко содержание кислот: насыщенных, полиненасыщенных и мононенасыщенных - природных антиоксидантов, также содержит естественный увлажнитель - гиалуроновую кислоту. Рекомендовано для тонизации и глубокого питания кожи: масло прекрасно смягчает, делает упругой, нежной, бархатистой, подтянутой и эластичной, предотвращает образование мимических морщин и преждевременное старение кожи.', 600),
       ('Масло черного тмина', 'Семена черного тмина, издавна используются как пряная и ароматная приправа. Об удивительных свойствах этого продукта упоминали в медицинских трактатах Гиппократ и фармаколог Древней Греции Диоскорид. Жителями Древнего Египта это масло применялось в качестве косметического средства и как компонент противоядия при укусах змей.', 2200),
       ('Смесь салатная', 'Смесь можно добавлять в салаты, каши, йогурты, в любые блюда по вашему желанию! Является прекрасным источником клетчатки', 100);
